[templates.add main]
	<html>
  	<head>
    	<title>PayU Implementation</title>
      <script src="https://code.jquery.com/jquery-3.6.0.min.js" integrity="sha256-/xUj+3OJU5yExlq6GSYGSHk7tPXikynS7ogEvDej/m4=" crossorigin="anonymous"></script>
      
      <style>
      #payu{
      	padding: 20px;
        background-color: orange;
        color: #fff;
      }
      </style>
    </head>
    
    <body>
    	<button id="payu">PayU</button>
      
      <script>
      	$(document).ready(()=>{
        	
          $('#payu').click(()=>{
          	 	var pay_load = {};
              pay_load.amount = 100;
              pay_load.txnId = 'T1234567890';
              pay_load.productName = 'JS T-Shirt';
              pay_load.firstName = 'John';
              pay_load.email = 'john@example.com';
              pay_load.phone = '9847012345';

              pay_load = JSON.stringify(pay_load);
          		window.ReactNativeWebView.postMessage(`{"action":"payu_pay", "message":${pay_load}}`);
          })
        
        	window.addEventListener("message", message => {
            let json = message.data;
            switch(json.action){
              case 'payu_pay_res':
              	let reply = json.message;
                
                alert(JSON.stringify(reply));
                
              break;
              default: console.log('unknown event recieved');
            }
    			});
        })
      </script>
    </body>
  </html>
[/templates.add]

