   window.addEventListener("message", message => {
      let json_data = message.data;
      switch(json_data.action){
        case 'getToken':
  				$.post( "[app.path /]/[module.get slug /].save_token", { token: json_data.token} );
          $("#token").empty().text(json_data.token);
        break;
        default: console.log('unknown event recieved');
      }
    });
