<script type="spa/axn" axn=core.run_script>
  spa.record={};
  spa.record.prerequisites={};
	spa.ajax_count = 1;                      
  spa.record.prerequisites.js_files='';

  spa.record.init=function(){}
    $( "#video" ).click(function() {
    	spa.record.startRecord();  	
      $(".item-title").empty().html("<b>Recording.....</b><br>");
    });

		//** Video recording code **//
    spa.record.startRecord=function(){
    	var mediaConstraints = {
          audio: true,
          video: true
        };
        navigator.getUserMedia(mediaConstraints, spa.record.onMediaSuccess, spa.record.onMediaError);

       /*window.navigator.mediaDevices.getUserMedia(mediaConstraints).then((stream)=>{
        alert("Working...");
            // let videoTrack = stream.getVideoTracks()[0];
            // console.log(videoTrack);
            //document.querySelector("video").srcObject = stream;
            console.log(stream);
        }).catch(e=>{
        		        		alert(JSON.stringify(e));
        								alert(e.name)
                        alert(e.message)
                        alert(e.code)
                      })
                      */
    }
	  spa.record.onMediaSuccess=function(stream){

        var options = {
            audioBitsPerSecond : 128000,
            videoBitsPerSecond : 144000,
            mimeType : 'video/webm'
        }
        spa.record.mediaRecorder = new MediaRecorder(stream,options);

          spa.record.mediaRecorder.mimeType = 'video/webm';
          spa.record.mediaRecorder.ondataavailable = function (event) {

            //console.log("data-available");
            //console.log(event);

            // POST/PUT "Blob" using FormData/XHR2
            var file_name = 'msr-' + (new Date).toISOString().replace(/:|\./g, '-') + '.webm';

            var file = new File([event.data], 'msr-' + (new Date).toISOString().replace(/:|\./g, '-') + '.webm', {
                type: 'video/webm'
            });
            //console.log(file);

            // create FormData
            var formData = new FormData();
            formData.append('video_filename', file_name);
            formData.append('quiz_id', "[aw2.get module.assignment_id /]");
            formData.append("video_blob", event.data, file_name);
            var request = new XMLHttpRequest();
            request.onreadystatechange = function(data) {
                if (request.readyState == 4)
                {

                  if(request.status == 200) {
                     //console.log('File uploaded to this path:');
             				if(spa.ajax_count === 2){
                      //var data = JSON.parse(data);
                      $(".item-title").empty().html("<b>Recorded video Preview</b><br>");
                      $("#video-res").empty().html(request.response);
                    }
                    stream.getTracks().forEach(track => track.stop())

                  }
                }
            };
            spa.ajax_count++;
            request.open('POST', "[app.path /]/[module.get slug /].pipedata");
            request.send(formData);
        };
         // spa.record.mediaRecorder.start(30000); //60000 = 1 min
          spa.record.mediaRecorder.start(30000); //60000 = 1 min

        	/* to stop media spa.record.mediaRecorder.stop();*/  
    }
    spa.record.onMediaError=function(e){
    	alert(e.name)
    	alert(e.PERMISSION_DENIED)
    	alert(e.code)
      console.error('media error', e);
    }  
	</script>
</main>

